 0.20e2 - sqrt(0.147e3 - 0.42e2 * sqrt(0.7e1)) * sqrt(0.7e1) / 0.40e2 + sqrt(0.147e3 + 0.42e2 * sqrt(0.7e1)) * sqrt(0.7e1) / 0.40e2 - sqrt(0.147e3 + 0.42e2 * sqrt(0.7e1)) / 0.20e2 -sqrt(0.7e1) / 0.15e2 + 0.7e1 / 0.60e2 + sqrt(0.147e3 - 0.42e2 * sqrt(0.7e1)) * sqrt(0.7e1) / 0.90e2 + sqrt(0.147e3 - 0.42e2 * sqrt(0.7e1)) / 0.180e3; sqrt(0.7e1) / 0.15e2 - 0.7e1 / 0.60e2 - sqrt(0.147e3 - 0.42e2 * sqrt(0.7e1)) * sqrt(0.7e1) / 0.90e2 - sqrt(0.147e3 - 0.42e2 * sqrt(0.7e1)) / 0.180e3 sqrt(0.147e3 - 0.42e2 * sqrt(0.7e1)) * sqrt(0.7e1) / 0.40e2 + sqrt(0.147e3 - 0.42e2 * sqrt(0.7e1)) / 0.20e2 - sqrt(0.147e3 + 0.42e2 * sqrt(0.7e1)) * sqrt(0.7e1) / 0.40e2 + sqrt(0.147e3 + 0.42e2 * sqrt(0.7e1)) / 0.20e2 -0.4e1 / 0.45e2 * sqrt(0.147e3 - 0.42e2 * sqrt(0.7e1)) - sqrt(0.147e3 - 0.42e2 * sqrt(0.7e1)) * sqrt(0.7e1) / 0.36e2 0 sqrt(0.147e3 - 0.42e2 * sqrt(0.7e1)) * sqrt(0.7e1) / 0.40e2 + sqrt(0.147e3 - 0.42e2 * sqrt(0.7e1)) / 0.20e2 + sqrt(0.147e3 + 0.42e2 * sqrt(0.7e1)) * sqrt(0.7e1) / 0.40e2 - sqrt(0.147e3 + 0.42e2 * sqrt(0.7e1)) / 0.20e2 -sqrt(0.7e1) / 0.15e2 + 0.7e1 / 0.60e2 - sqrt(0.147e3 - 0.42e2 * sqrt(0.7e1)) * sqrt(0.7e1) / 0.90e2 - sqrt(0.147e3 - 0.42e2 * sqrt(0.7e1)) / 0.180e3; -sqrt(0.7e1) / 0.15e2 - 0.7e1 / 0.60e2 + sqrt(0.147e3 + 0.42e2 * sqrt(0.7e1)) * sqrt(0.7e1) / 0.90e2 - sqrt(0.147e3 + 0.42e2 * sqrt(0.7e1)) / 0.180e3 sqrt(0.147e3 + 0.42e2 * sqrt(0.7e1)) * sqrt(0.7e1) / 0.36e2 - 0.4e1 / 0.45e2 * sqrt(0.147e3 + 0.42e2 * sqrt(0.7e1)) sqrt(0.147e3 - 0.42e2 * sqrt(0.7e1)) * sqrt(0.7e1) / 0.40e2 + sqrt(0.147e3 - 0.42e2 * sqrt(0.7e1)) / 0.20e2 - sqrt(0.147e3 + 0.42e2 * sqrt(0.7e1)) * sqrt(0.7e1) / 0.40e2 + sqrt(0.147e3 + 0.42e2 * sqrt(0.7e1)) / 0.20e2 -sqrt(0.147e3 - 0.42e2 * sqrt(0.7e1)) * sqrt(0.7e1) / 0.40e2 - sqrt(0.147e3 - 0.42e2 * sqrt(0.7e1)) / 0.20e2 - sqrt(0.147e3 + 0.42e2 * sqrt(0.7e1)) * sqrt(0.7e1) / 0.40e2 + sqrt(0.147e3 + 0.42e2 * sqrt(0.7e1)) / 0.20e2 0 sqrt(0.7e1) / 0.15e2 + 0.7e1 / 0.60e2 + sqrt(0.147e3 + 0.42e2 * sqrt(0.7e1)) * sqrt(0.7e1) / 0.90e2 - sqrt(0.